import {ReactP5Wrapper} from "react-p5-wrapper";
import {NavLink} from "react-router-dom";

const activeStyle = {
    color: 'red',
    borderBottom: '1em, solid, black',
}

function Tab() {
    return (
        <div style={{paddingTop: "20px"}}>
            <nav>
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/1"} key={'1'}>{"Project 1"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/2"} key={'2'}>{"Project 2"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/3"} key={'2'}>{"Project 3"}</NavLink>
            </nav>
        </div>
    );
}
// = .= This is the homework 3
let bugs = [];
function sketch(p5) {
    let i;
    p5.setup = () => {
    p5.createCanvas(500, 500);

        p5.background('yellow');
    }

    p5.draw = () => {
        p5.background('yellow');
        p5.ellipse(p5.mouseX, p5.mouseY, 25, 25);

        for (i = 0; i < bugs.length; i++){
            bugs[i].move();
            bugs[i].display();
        }
    }

    p5.mousePressed = () => {
        bugs[i] = new Bug(p5.mouseX, p5.mouseY);
        bugs.push(bugs[i]);
    }

    class Bug {
        constructor(x,y){
            this.x = x;
            this.y = y;
            this.diameter = p5.random(10, 50);
            this.speed = 1;
        }

        move(){
            this.x += p5.random(-this.speed, this.speed);
            this.y += p5.random(-this.speed, this.speed);
        }

        display(){
            p5.ellipse(this.x, this.y, this.diameter, this.diameter)
        }
    }

}
function resetSketch(){
    bugs = new Array;
    console.log();
}
export function Bug() {
    return (
        <body style={{background:'red'}}>
            <div style={{textAlign: "center", fontFamily: "Times New Roman, Times, serif"}}>
            <div>
                <Tab/>
                <b>Click or move your mouse to Interact</b>
            </div>
            <div style={{paddingTop: "20px", textAlign: "center"}}>
                <ReactP5Wrapper sketch={sketch} >
                </ReactP5Wrapper>
            </div>
            <button onClick={resetSketch}>
                Reset
            </button>
        </div>
        </body>
    );
}
