import {NavLink} from "react-router-dom";

const activeStyle = {
    color: 'red',
    borderBottom: '1em, solid, black',
}

var bugs = [];
var i;
function Tab() {
    return (
        <div style={{paddingTop: "20px"}}>
            <nav>
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/1"} key={'1'}>{"Project 1"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/2"} key={'2'}>{"Project 2"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/3"} key={'2'}>{"Project 3"}</NavLink>
            </nav>
        </div>
    );
}




function setup(){
    let canvas = this.createCanvas(500, 500);
    canvas.parent("myContainer")
    this.background('yellow');
    resetSketch();
}

function resetSketch(){
    bugs = new Array;
}

function draw(){
    this.background('yellow');
    this.ellipse(this.mouseX, this.mouseY, 25, 25);

    for (var i = 0; i < bugs.length; i++){
        bugs[i].move();
        bugs[i].display();
    }
}

function mousePressed(){
    bugs[i] = new Bug(this.mouseX, this.mouseY);
    bugs.push(bugs[i]);
}

class Bug {
    constructor(x,y){
        this.x = x;
        this.y = y;
        this.diameter = this.random(10, 50);
        this.speed = 1;
    }

    move(){
        this.x += this.random(-this.speed, this.speed);
        this.y += this.random(-this.speed, this.speed);
    }

    display(){
        this.ellipse(this.x, this.y, this.diameter, this.diameter)
    }
}

export function Homework3(){
    return (
        <>
            
            <div>
                <h1>P5 in HTML</h1>
                <p>Putting bugs as much as you want to the canvas by clicking mouse!</p>
            </div>
            <div class="main">
                <div id="myContainer">
                </div>
            </div>
            <button id="reset" onclick = {resetSketch()}>
                <p>Reset</p>
            </button>
        </>
    );
}
