import {NavLink} from "react-router-dom";

var bugs = [];
var i;
function Tab() {
    return (
        <div style={{paddingTop: "20px"}}>
            <nav>
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/1"} key={'1'}>{"Project 1"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/2"} key={'2'}>{"Project 2"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/3"} key={'2'}>{"Project 3"}</NavLink>
            </nav>
        </div>
    );
}


function setup(){
    let canvas = createCanvas(500, 500);
    canvas.parent("myContainer")
    background('yellow');
    resetSketch();
}

function resetSketch(){
    bugs = new Array;
}

function draw(){
    background('yellow');
    ellipse(mouseX, mouseY, 25, 25);

    for (var i = 0; i < bugs.length; i++){
        bugs[i].move();
        bugs[i].display();
    }
}

function mousePressed(){
    bugs[i] = new Bug(mouseX, mouseY);
    bugs.push(bugs[i]);
}

class Bug {
    constructor(x,y){
        this.x = x;
        this.y = y;
        this.diameter = random(10, 50);
        this.speed = 1;
    }

    move(){
        this.x += random(-this.speed, this.speed);
        this.y += random(-this.speed, this.speed);
    }

    display(){
        ellipse(this.x, this.y, this.diameter, this.diameter)
    }
    contain(bx, by){
        return Math.sqrt(sq(mouseX-bx) + sq(mouseY-by)) < this.diameter/4 ? true : false;
    }

}

export function Homework3(){
    return (
        <>

            <div>
                <h1>P5 in HTML</h1>
                <p>Putting bugs as much as you want to the canvas by clicking mouse!</p>
            </div>
            <div class="main">
                <div id="myContainer">
                </div>
            </div>
            <button id="reset" onclick = {resetSketch()}>
                <p>Reset</p>
            </button>
        </>
    );
}
