import {useState} from 'react'

function NasaClass(){
  const [desc, setDesc] = useState("")
  const [url, setUrl] = useState("")

  function updateState(desc, url){
    setDesc(desc)
    setUrl(url)
  }
  function getImage(){
    const api = "MZwupgqfb2TSs5fb582TMbtyLSfhqy5CBiJRdzGB"
    const url = 'https://api.nasa.gov/planetary/apod?api_key=${api}'
    fetch(url).then(response => response.json())
          .then(data => {updateState(data.explanation, data.url)})
  } 
  return(
    <div>
      <button onClick={getImage}>Get Image</button>
      <img src={url} alt={'Nasa apod api'}/>
      <section>
          <p>{desc}</p>
      </section>
    </div>
  )
}
