import { Component } from "react";

const activeStyle = {
    color: 'red',
    borderBottom: '1em, solid, black',
}

function Tab() {
    return (
        <div style={{paddingTop: "20px"}}>
            <nav>
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/1"} key={'1'}>{"Project 1"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/2"} key={'2'}>{"Project 2"}</NavLink> {"| "}
                <NavLink style={({ isActive }) => isActive ? activeStyle : undefined}
                         to={"/projects/3"} key={'2'}>{"Project 3"}</NavLink>
            </nav>
        </div>
    );
    }

export default class NasaClass extends Component {
    constructor(props) {
        super(props);

        this.state = {description: "", url: ""}
    }

    updateState = (desc, url) => {
        this.setState({description: desc, url: url})
    }

    getImage = () => {
        const api = "MZwupgqfb2TSs5fb582TMbtyLSfhqy5CBiJRdzGB"
        const url = `https://api.nasa.gov/planetary/apod?api_key=${api}`
        fetch(url).then(response => response.json())
            .then(data => this.updateState(data.explanation, data.url))
    }

    render() {
        return(
            <div style={{textAlign:"center", color:(122, 184, 191, 1)}} >
                <button onClick={this.getImage}>Get Image</button>
                <img width="50%" height="50%" src={this.state.url} alt={'Nasa apod api'}/>
                <section>
                    <p>{this.state.description}</p>
                </section>
            </div>
        )
    }
}
